mapper.select:
SELECT m_id, m_pw, m_name, m_email FROM member
WHERE m_id = #{m_id}
AND m_pw = #{m_pw}

mapper.update:
UPDATE member SET
m_pw = #{m_pw};
m_name = #{m_name}
WHERE m_id = #{m_id}

DELETE FROM member
WHERE m_id = #{m_id}
AND m_pw = #{m_pw}

mapper.select:
SELECT COUNT(*) FROM member
WHERE m_id = #{m_id}
AND m_pw = #{m_pw}
